/**
 * Template-specific reusable elements (variables, mixins, abstract classes)
 */
body {
  font-family: Times New Roman, serif; }

* {
  prince-footnote-policy: keep-with-block;
  prince-bookmark-label: none; }

p {
  text-indent: 0.7rem; }

figure {
  background: #FDFFF8;
  padding: 0.7rem;
  font-size: 0.77rem; }

a {
  color: inherit;
  text-decoration: none;
  border-bottom: 1px solid #F32E36; }

h1, h2, h3, h4, h5, h6 {
  font-family: Roboto, serif; }

/**
 * Notes styling
 */
.footnote {
  display: prince-footnote;
  counter-increment: footnote;
  footnote-display: inline; }

.footnote-content {
  font-size: 0.77rem; }
  .footnote-content .footnote-p,
  .footnote-content .footnote-h1,
  .footnote-content .footnote-h2,
  .footnote-content .footnote-h3,
  .footnote-content .footnote-h4,
  .footnote-content .footnote-h5,
  .footnote-content .footnote-h6,
  .footnote-content .footnote-ul,
  .footnote-content .footnote-ol,
  .footnote-content .footnote-li,
  .footnote-content .footnote-pre,
  .footnote-content .footnote-blockquote {
    display: block; }
  .footnote-content .footnote-p {
    text-indent: 0.7rem; }
  .footnote-content .footnote-h1,
  .footnote-content .footnote-h2,
  .footnote-content .footnote-h3,
  .footnote-content .footnote-h4,
  .footnote-content .footnote-h5,
  .footnote-content .footnote-h6 {
    font-size: 1.232rem; }

.footnote::footnote-call {
  content: counter(footnote);
  font-size: 83%;
  vertical-align: super;
  line-height: none; }

@page {
  @footnotes {
    padding-top: 0.7rem; } }

img {
  width: 9999em;
  max-width: 100%;
  height: auto;
  max-height: 100%; }

.section-container {
  counter-reset: footnote; }

@page {
  @bottom {
    content: counter(page); } }

.table-of-contents-container a::after,
.table-of-figures-container a::after {
  content: leader(".") target-counter(attr(href), page); }

.page-link {
  content: target-counter(attr(href), page); }

.separate-pages {
  page-break-before: always;
  page-break-after: always; }

.bookmark-level-0 {
  prince-bookmark-level: 1; }

.bookmark-level-1 {
  prince-bookmark-level: 2; }

.bookmark-level-2 {
  prince-bookmark-level: 3; }

.bookmark-level-3 {
  prince-bookmark-level: 4; }

.bookmark-level-4 {
  prince-bookmark-level: 5; }

.bookmark-level-5 {
  prince-bookmark-level: 6; }

.bookmark-level-6 {
  prince-bookmark-level: 7; }

.bookmark-title {
  prince-bookmark-label: attr(title); }

/**
 * Cover styling
 */
front-cover-container {
  page-break-after: always;
  page-break-before: never; }

#cover-image {
  position: absolute;
  min-height: 100%; }

.cover-content {
  position: absolute;
  left: 1rem;
  top: 1rem; }

@page :first {
  margin: 0;
  page-break-after: always;
  @bottom {
    content: none; } }

@page :last {
  margin: 0;
  page-break-before: always;
  @bottom {
    content: none; } }


.back-cover-container {
  page: back-cover;
  page-break-before: always; }

.table-of-contents {
  page: table-of-contents; }

/**
 * Special pages
 */
.mentions-container {
  padding: 0;
  margin: 0; }
  .mentions-container ul {
    padding: 0;
    margin: 0; }
  .mentions-container li {
    list-style-type: none;
    padding: 0;
    margin-bottom: 1.1662rem; }
  .mentions-container .mentions-list {
    font-size: 0.77rem;
    margin-top: 0.7rem;
    font-style: italic;
    text-indent: 0.7rem; }

.end-notes {
  padding: 0;
  margin: 0; }
  .end-notes li {
    margin: 0;
    padding: 0;
    margin-bottom: 1.1662rem;
    list-style-type: none; }
    .end-notes li > div {
      margin-top: -1.848rem;
      margin-left: 1.1662rem; }

/**
 * Figures
 */
table {
  max-width: 100%;
  font-size: 0.77rem;
  font-family: Roboto, serif;
  text-align: left;
  width: 90%;
  border-collapse: collapse;
  border: solid 1px #414E4D;
  font-weight: 200; }

table thead tr {
  background: #414E4D;
  color: white; }

table tbody tr:nth-child(even) {
  background: #FDFFF8; }

table th {
  border: solid 1px #414E4D;
  padding: 0.7rem; }
